package day8;

public class ex01두수의합메서드 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		System.out.println(re(4,6)); //안에 숫자가 바뀌면  ((메소드에 주어진 공식)) 대로 값이 나온다
	}//메인 end

	
	/*기능 : 두 정수의 합을 알려주는 메소드
	매개변수 : int a, int b
	리턴타입 : int
	메소드명 : re
	*/
	
	public static int re(int a, int b) {
		  return a+b;
		}
		
		
		
	}//메소드 end
	
	
	

